import React, { Component } from "react";
import "../assets/search_result.css";
import restaurant from "./restaurant_card";

class Search_results extends Component {
  state = { open_restaurants: [], closed_restaurants: [] };
  constructor() {
    super();
    console.log("now it gets initialized!");
    var i;
    var open_rest_arr = [];
    var closed_rest_arr = [];
    var restaurants = this.props.restaurant_array;
    for (i = 0; i < restaurants.length; i++) {
      if (this.is_open(restaurants[i])) open_rest_arr.push(restaurants[i]);
      else closed_rest_arr.push(restaurants[i]);
    }
    this.setState({
      open_restaurants: open_rest_arr,
      closed_restaurants: closed_rest_arr
    });
    this.initialize_restaurants();
  }
  initialize_restaurants = () => {};
  is_open(restaurant) {
    return true;
  }
  render() {
    return (
      <div>
        {console.log(this.props.restaurant_array.length)}
        <img
          id="first_image"
          className="img-responsive"
          src={require("../images/restaurant-search-banner-2x.jpg")}
          alt="Chania"
        />
        <h3 className="text-right">
          {123}
          رستوران امکان سرویس‌دهی به
          <abbr>{" تهران "}</abbr>را دارند
        </h3>
        <hr />
        <div className="search-container">
          <input
            type="text"
            id="area_search"
            placeholder="جست‌و‌جوی رستوران در این محدوده"
            name="search"
          />
          <button type="submit">
            <i className="fa fa-search" id="my_search_botton" />
          </button>
        </div>
        <div id="main_panel" className="row">
          <div className="restuarant_section col-sm-9">
            <div className="inner_rest_sec">
              <div className="restaurant_subsection">
                {this.state.open_restaurants.map(restaurant => (
                  <restaurant rest_info={restaurant} />
                ))}
              </div>
              <div class="w-100" />
              <div className="restaurant_subsection">
                {this.state.closed_restaurants.map(restaurant => (
                  <restaurant rest_info={restaurant} />
                ))}
              </div>
            </div>
          </div>
          <div className="category_section col-sm-3">
            <div className="inner_cat_sec">som cat</div>
          </div>
        </div>
      </div>
    );
  }
}

export default Search_results;
